<? include 'functions.php'; db_connect('192.168.1.101','core','root','zerohour'); if ($_GET[core]){ $q="SELECT * FROM version_control where core='$_GET[core]' order by core_version DESC"; $r=@mysql_query($q); $d=mysql_fetch_array($r, MYSQL_ASSOC); $string = "$_GET[core] v$d[core_version] $d[release_date]"; } else{ $string = "NO CORE SELECTED"; } $image = imagecreate(250, 20); imagecolorallocate($image, 0, 0, 0); $fontColor = imagecolorallocate($image, 255, 255, 255); $startX     = 2; $startY     = 3; imagestring($image, 3, $startX, $startY, $string, $fontColor); header("Content-type: image/png"); imagepng($image);?>